'use strict'
angular.module('simple.team.auth', []).service('AuthService', [
  '$http', function($http) {
    this.user
    this.loadUser = function() {
      return $http.get('/api/users/me').success((function(_this) {
        return function(data) {
          return _this.user = data.user
        }
      })(this))
    }
  }
])

