angular.module('simple.team.focusMe', []).directive('focusMe', [
  '$timeout', function($timeout) {
    return {
      scope: {
        trigger: '=focusMe'
      },
      link: function(scope, element) {
        return scope.$watch('trigger', function(value) {
          if (value === true) {
            return $timeout(function() {
              return element[0].focus()
            })
          }
        })
      }
    }
  }
])

